/** @type {import('tailwindcss').Config} */
import { tailwindcssOriginSafelist } from '@headlessui-float/vue'
import type { Config } from 'tailwindcss'

export default {
  content: [
    './index.html',
    './src/**/*.{vue,js,ts,jsx,tsx}',
  ],
  safelist: [...tailwindcssOriginSafelist],
  theme: {
    container: {
      center: true,
      padding: '2rem',
      screens: {
        '2xl': '1400px',
      },

    },
    fontSize: {
      hero: ['var(--font-size-hero)', {
        lineHeight: 'var(--font-line-height-hero)',
      }],
      title: ['var(--font-size-title)', {
        lineHeight: 'var(--font-line-height-title)',
      }],
      subtitle: ['var(--font-size-subtitle)', {
        lineHeight: 'var(--font-line-height-subtitle)',
      }],
      heading: ['var(--font-size-heading)', {
        lineHeight: 'var(--font-line-height-heading)',
      }],
      body: ['var(--font-size-body)', {
        lineHeight: 'var(--font-line-height-body)',
      }],
      subtext: ['var(--font-size-subtext)', {
        lineHeight: 'var(--font-line-height-subtext)',
      }],
      caption: ['var(--font-size-caption)', {
        lineHeight: 'var(--font-line-height-caption)',
      }],
    },

    boxShadow: {
      'DEFAULT': 'var(--shadow)',
      'card-shadow': 'var(--shadow-card);',
      'card-hover-shadow': 'var(--shadow-card-hover);',
      'button-shadow': 'var(--shadow-button);',
      'button-hover-shadow': 'var(--shadow-button-hover);',
      'popover-shadow': 'var(--shadow-popover);',
      'modal-shadow': 'var(--shadow-modal);',
      'input-shadow': 'var(--shadow-input);',
    },

    borderRadius: {
      DEFAULT: 'var(--radius)',
      card: 'var(--radius-card)',
      button: 'var(--radius-button)',
      modal: 'var(--radius-modal)',
      popover: 'var(--radius-popover)',
      full: 'var(--radius-full)',
      input: 'var(--radius-input)',
    },

    colors: {
      transparent: 'transparent',
      black: 'hsl(var(--black))',
      white: 'hsl(var(--white))',

      border: 'hsl(var(--border))',
      ring: 'hsl(var(--ring))',

      background: 'hsl(var(--background))',
      foreground: 'hsl(var(--foreground))',

      primary: {
        DEFAULT: 'hsl(var(--primary))',
        foreground: 'hsl(var(--primary-foreground))',
      },
      secondary: {
        DEFAULT: 'hsl(var(--secondary))',
        foreground: 'hsl(var(--secondary-foreground))',
      },
      destructive: {
        DEFAULT: 'hsl(var(--destructive))',
        foreground: 'hsl(var(--destructive-foreground))',
      },
      muted: {
        DEFAULT: 'hsl(var(--muted))',
        foreground: 'hsl(var(--muted-foreground))',
      },
      accent: {
        DEFAULT: 'hsl(var(--accent))',
        foreground: 'hsl(var(--accent-foreground))',
      },
      warn: {
        DEFAULT: 'hsl(var(--warning))',
        foreground: 'hsl(var(--warning-foreground))',
      },
      success: {
        DEFAULT: 'hsl(var(--success))',
        foreground: 'hsl(var(--success-foreground))',
      },
      popover: {
        DEFAULT: 'hsl(var(--popover))',
        foreground: 'hsl(var(--popover-foreground))',
      },
      card: {
        DEFAULT: 'hsl(var(--card))',
        foreground: 'hsl(var(--card-foreground))',
      },

      input: {
        DEFAULT: 'hsl(var(--input))',
        foreground: 'hsl(var(--input-foreground))',
        placeholder: 'hsl(var(--input-placeholder))',
        disabled: 'hsl(var(--input-disabled))',
        border: 'hsl(var(--input-border))',
        ring: 'hsl(var(--input-ring))',
      },

      neutral: {
        900: 'hsl(var(--neutral-900))',
        800: 'hsl(var(--neutral-800))',
        700: 'hsl(var(--neutral-700))',
        600: 'hsl(var(--neutral-600))',
        500: 'hsl(var(--neutral-500))',
        400: 'hsl(var(--neutral-400))',
        300: 'hsl(var(--neutral-300))',
        200: 'hsl(var(--neutral-200))',
        100: 'hsl(var(--neutral-100))',
      },
    },
    extend: {
      transitionTimingFunction: {
        'out-back': 'cubic-bezier(0.34, 1.56, 0.64, 1)',
        'native': 'ease',
      },
    },
  },
} satisfies Config

@tailwind base;
@tailwind components;
@tailwind utilities;

@layer base {
  :root {
    /* #region COLORS */
    --black: 0 0% 0%;
    --white: 0 0% 100%;

    --background: 0 0% 100%;
    --foreground: 0 0% 0%;

    --primary: 260 77% 70%;
    --primary-foreground: 0 0% 100%;

    --secondary: 267 100% 96%;
    --secondary-foreground: 256 48% 42%;

    --accent: 210 40% 96.1%;
    --accent-foreground: 222.2 47.4% 11.2%;

    --destructive: 4 86% 58%;
    --destructive-foreground: 0 0% 100%;

    --success: 152 77% 39%;
    --success-foreground: 0 0% 100%;

    --warning: 34 94% 50%;
    --warning-foreground: 0 0% 100%;

    --muted: 210 20% 98%;
    --muted-foreground: 221 13% 46%;

    --popover: 0 0% 100%;
    --popover-foreground: 0 0% 0%;

    --card: 0 0% 100%;
    --card-foreground: 0 0% 0%;

    --border: 220 17% 96%;
    --input: 0 0% 100%;

    --neutral-100: 220 22% 96%;
    --neutral-200: 220 17% 93%;
    --neutral-300: 216 16% 84%;
    --neutral-400: 218 15% 65%;
    --neutral-500: 220 13% 46%;
    --neutral-600: 215 18% 34%;
    --neutral-700: 217 23% 27%;
    --neutral-800: 215 32% 17%;
    --neutral-900: 221 43% 11%;

    --ring: 264 100% 98%;

    --input: 0 0% 100%;
    --input-foreground: 221 43% 11%;
    --input-placeholder: 220 13% 46%;
    --input-disabled: 210 24% 98%;
    --input-border: 216 16% 84%;
    --input-ring: 265 89% 86%;
    /*#endregion*/

    /* #region SHADOWS */
    --shadow-button: 0;
    --shadow-button-hover: 0;

    --shadow-card: 0px 4px 6px -2px rgba(16, 24, 40, 0.03), 0px 12px 16px -4px rgba(16, 24, 40, 0.08);
    --shadow-card-hover: 0;

    --shadow-modal: 0px 4px 6px -2px rgba(16, 24, 40, 0.03), 0px 12px 16px -4px rgba(16, 24, 40, 0.08);
    --shadow-popover: 0px 8px 8px -4px rgba(16, 24, 40, 0.03), 0px 20px 24px -4px rgba(16, 24, 40, 0.08);
    --shadow-input: 0;
    /*#endregion*/



    /* #region RADIUS */
    --radius: 0.5rem;
    --radius-modal: 0.75rem;
    --radius-popover: 0.75rem;
    --radius-card: 1rem;
    --radius-button: 0.5rem;
    --radius-full: 9999px;
    --radius-input: 0.25rem;
    /* #endregion */

    /* #region FONT-SIZE */
    --font-size-hero: 3rem;
    --font-size-title: 1.5rem;
    --font-size-subtitle: 1.25rem;
    --font-size-heading: 1.125rem;
    --font-size-body: 1rem;
    --font-size-subtext: 0.875rem;
    --font-size-caption: 0.75rem;

    --font-line-height-hero: 1;
    --font-line-height-title: 2rem;
    --font-line-height-subtitle: 1.75rem;
    --font-line-height-heading: 1.75rem;
    --font-line-height-body: 1.5rem;
    --font-line-height-subtext: 1.25rem;
    --font-line-height-caption: 1rem;
    /* #endregion */

  }
}

@layer base {
  * {
    /* @apply border-border; */
  }

  body {
    /* @apply bg-background text-foreground; */
  }
}

/* Cant do this in tailwind, so I define it here */
@layer components {
  .transition-scale-bounce {
    transition: cubic-bezier(.57, .27, .11, 1.48) 0.25s transform, 0.2s opacity
  }
}
